// Size mapping
const sizeMap = {
  xs: 'w-3 h-3',
  sm: 'w-4 h-4',
  md: 'w-6 h-6',
  lg: 'w-8 h-8',
  xl: 'w-12 h-12',
  '2xl': 'w-16 h-16',
};

export const Icon: React.FC<IconProps> = ({ 
  icon, 
  className = '', 
  size = 'md',
  variant = 'outline',
  ...props 
}) => {
  const sizeClass = sizeMap[size];
  const baseClasses = `${sizeClass} ${className}`;
  
  // Get the path for the icon
  const iconPath = iconPaths[icon];
  
  if (!iconPath) {
    console.warn(`Icon "${icon}" not found. Using fallback.`);
    return (
      <svg
        className={baseClasses}
        fill="none"
        viewBox="0 0 24 24"
        stroke="currentColor"
        strokeWidth={variant === 'mini' ? 1 : 1.5}
        {...props}
      >
        <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v3.75m9-.75a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9 3.75h.008v.008H12v-.008Z" />
      </svg>
    );
  }

  // Handle brand icons differently (they use fill instead of stroke)
  const isBrandIcon = ['SupabaseIcon', 'NetlifyIcon', 'GitHubIcon'].includes(icon);
  
  if (isBrandIcon) {
    return (
      <svg
        className={baseClasses}
        fill="currentColor"
        viewBox="0 0 24 24"
        {...props}
      >
        <path d={iconPath} />
      </svg>
    );
  }

  // Regular outline icons
  return (
    <svg
      className={baseClasses}
      fill={variant === 'solid' ? 'currentColor' : 'none'}
      viewBox="0 0 24 24"
      stroke={variant === 'solid' ? 'none' : 'currentColor'}
      strokeWidth={variant === 'mini' ? 1 : 1.5}
      {...props}
    >
      <path 
        strokeLinecap="round" 
        strokeLinejoin="round" 
        d={iconPath} 
        fill={variant === 'solid' ? 'currentColor' : undefined}
      />
    </svg>
  );
};

export default Icon;